    routes: [
      {
        path: routes.pages.main, component: () => import('../pages/Main.vue')
      },
      {
        path: routes.pages.about, component: () => import('../pages/About.vue')
      }
    ]
  })
}

export default {
  pages: {
    about: 'about',
    main: '/'
  }
}

import Vue from 'vue'
import Vuex from 'vuex'

import main from './modules/main'

import config from '../config'

Vue.use(Vuex)

export function createStore () {
  return new Vuex.Store({
    strict: !config.isProduction,
    modules: {
      main
    }
  })
}

import axios from 'axios'

import {
  MAIN__ITEM_DELETE,
  MAIN__ITEM_ADD,
  MAIN__ITEM_ADD_ASYNC,
  MAIN__GET_DATA,
  MAIN__SET_DATA
} from '../const/main'

export default {
  namespaced: true,
  state: {
    items: [
      {
        id: 1,
        title: 'Milk'
      },
      {
        id: 2,
        title: 'Strawberry'
      },
      {
        id: 3,
        title: 'Egg'
      }
    ],
    dataFromApi: []
  },
  mutations: {
    [MAIN__ITEM_DELETE] (state, { id }) {
      state.items = state.items.filter(item => item.id !== id)
    },
    [MAIN__ITEM_ADD] (state, { item }) {
      const items = [...state.items]
      items.push(item)

      state.items = items
    },
    [MAIN__SET_DATA] (state, { data }) {
      state.dataFromApi = data
    }
  },
  actions: {
    [MAIN__ITEM_ADD_ASYNC] ({ commit }, { item }) {
      setTimeout(() => {
        commit(MAIN__ITEM_ADD, { item })
      }, 1000)
    },
    async [MAIN__GET_DATA] ({ commit }) {
      try {
        const resp = await axios.get('https://jsonplaceholder.typicode.com/posts')
        commit(MAIN__SET_DATA, { data: resp.data })
      } catch (err) {
        console.error('Error fetching data')
      }
    }
  }
}
